mapper.select:
select count(*)
from bill
where to_char(bill_timestamp, 'yyyy mm dd') = #{ date } and	userid = #{userid}


SELECT *  
FROM (SELECT ROWNUM RNUM, b.*
       FROM (SELECT * FROM bill 

			where to_char(bill_timestamp, 'yyyy mm dd') = #{ date } and userid = #{userid}


			where to_char(bill_timestamp, 'yyyy mm') = #{ date } and userid = #{userid}


			where userid = #{userid}

       ORDER BY BILL_TIMESTAMP ASC) b )  
where rnum >= #{startRow} and rnum <= #{endRow}
