mapper.select:
select * from member
where userid = #{ userid } and userpwd = #{ userpwd }
and loginok = 'Y'

mapper.update:
update member
set userpwd = ${ userpwd }
where userid = #{ userid } and email = #{ email }

update member
set loginok = #{ loginok }
where userid = #{ userid }

update member
set userpwd = #{ userpwd },
	email = #{ email }
where userid = #{ userid }
