mapper.insert:
insert into notice (noticeno, noticetitle, noticewriter, noticedate, original_filepath, rename_filepath, noticecontent, readcount, IMPORTANCE) values ((select max(noticeno) + 1 from notice), #{ noticetitle }, #{ noticewriter }, sysdate, #{ original_filepath, jdbcType=VARCHAR }, #{ rename_filepath, jdbcType=VARCHAR }, #{ noticecontent }, default,
#{ importance }
default
)

mapper.update:
update notice
set noticetitle = #{ noticetitle },
	noticecontent = #{ noticecontent}

	, original_filepath = #{ original_filepath }
	, rename_filepath = #{ rename_filepath }


	, original_filepath = null
	, rename_filepath = null


	, importance = #{ importance }


	, importance = default

where noticeno = #{ noticeno }

update notice
set readcount = readcount + 1
where noticeno = #{ noticeno }
